{"code": "# Perform BFS to check if paper 4 can be reached from paper 23
from collections import deque

def can_reach_paper_4_from_23(graph):
    # If paper 23 is not in the graph, it can't cite anything
    if 23 not in graph.nodes:
        return False

    # If paper 4 is not in the graph, it can't be cited
    if 4 not in graph.nodes:
        return False

    # BFS to find if paper 4 is reachable from paper 23
    visited = set()
    queue = deque([23])
    visited.add(23)

    while queue:
        current_paper = queue.popleft()

        # Get papers that cite the current paper
        if current_paper in graph.edges:
            citing_papers = graph.edges[current_paper]

            for citing_paper in citing_papers:
                if citing_paper == 4:
                    return True  # Found paper 4!

                if citing_paper not in visited:
                    visited.add(citing_paper)
                    queue.append(citing_paper)

    return False  # Paper 4 not found

