    conjunctions = {'and', 'or', 'but', 'so', 'yet', 'for', 'nor'}
    modals = {'can', 'could', 'may', 'might', 'must', 'shall', 'should', 'will', 'would'}
    common_verbs = {'is', 'are', 'was', 'were', 'be', 'been', 'being', 'have', 'has', 'had', 'do', 'does', 'did', 'get', 'got', 'make', 'made', 'go', 'went', 'come', 'came', 'take', 'took', 'see', 'saw', 'know', 'knew', 'think', 'thought', 'say', 'said', 'tell', 'told', 'give', 'gave', 'find', 'found', 'work', 'worked', 'call', 'called', 'try', 'tried', 'ask', 'asked', 'need', 'needed', 'feel', 'felt', 'become', 'became', 'leave', 'left', 'put', 'kept', 'keep', 'held', 'hold', 'turn', 'turned', 'start', 'started', 'show', 'showed', 'hear', 'heard', 'play', 'played', 'run', 'ran', 'move', 'moved', 'live', 'lived', 'believe', 'believed', 'bring', 'brought', 'happen', 'happened', 'write', 'wrote', 'provide', 'provided', 'sit', 'sat', 'stand', 'stood', 'lose', 'lost', 'pay', 'paid', 'meet', 'met', 'include', 'included', 'continue', 'continued', 'set', 'learn', 'learned', 'change', 'changed', 'lead', 'led', 'understand', 'understood', 'watch', 'watched', 'follow', 'followed', 'stop', 'stopped', 'create', 'created', 'speak', 'spoke', 'read', 'allow', 'allowed', 'add', 'added', 'spend', 'spent', 'grow', 'grew', 'open', 'opened', 'walk', 'walked', 'win', 'won', 'offer', 'offered', 'remember', 'remembered', 'love', 'loved', 'consider', 'considered', 'appear', 'appeared', 'buy', 'bought', 'wait', 'waited', 'serve', 'served', 'die', 'died', 'send', 'sent', 'expect', 'expected', 'build', 'built', 'stay', 'stayed', 'fall', 'fell', 'cut', 'reach', 'reached', 'kill', 'killed', 'remain', 'remained'}

    # Split text into words and clean punctuation
    import re
    words = re.findall(r"\b\w+\b|[^\w\s]", text.lower())

    result = []

    for i, word in enumerate(words):
        tag = 'NN'  # default to noun

        # Skip punctuation for now, tag as SYM
        if not word.isalpha():
            if word.isdigit():
                tag = 'CD'
            else:
                tag = 'SYM'
        # Determiners
        elif word in determiners:
            tag = 'DT'
        # Prepositions  
        elif word in prepositions:
            tag = 'IN'
        # Pronouns
        elif word in pronouns:
            tag = 'PRP'
        # Possessive pronouns
        elif word in possessive_pronouns:
            tag = 'PRP$'
        # Conjunctions
        elif word in conjunctions:
            tag = 'CC'
        # Modals
        elif word in modals:
            tag = 'MD'
        # Common verbs
        elif word in common_verbs:
            if word.endswith('ing'):
                tag = 'VBG'
            elif word.endswith('ed'):
                tag = 'VBD'
            elif word in ['is', 'are', 'am']:
                tag = 'VBZ' if word == 'is' else 'VBP'
            elif word in ['was', 'were']:
                tag = 'VBD'
            else:
                tag = 'VB'
        # Word ending patterns
        elif word.endswith('ly'):
            tag = 'RB'
        elif word.endswith('ing'):
            tag = 'VBG'
        elif word.endswith('ed'):
            tag = 'VBD'
        elif word.endswith('er') and len(word) > 3:
            tag = 'JJR'  # could be comparative adjective
        elif word.endswith('est') and len(word) > 4:
            tag = 'JJS'  # superlative adjective
        elif word.endswith('s') and len(word) > 2:
            tag = 'NNS'  # plural noun
        # Capitalization patterns (proper nouns)
        elif word[0].isupper() and i > 0:  # Not first word
            tag = 'NNP'

        result.append((word, tag))

    return result"}

