    if start not in graph.edges:
        return False

    visited = set()
    queue = [start]

    while queue:
        current = queue.pop(0)
        if current == target:
            return True

        if current in visited:
            continue
        visited.add(current)

        # Add all nodes that current cites (outgoing edges)
        if current in graph.edges:
            for neighbor in graph.edges[current]:
                if neighbor not in visited:
